<?php if (!defined('basePath')) exit('No direct script access allowed');$postsId 		= intval($this->uri(2));$posts 	 		= new stdClass;$posts->getRow 	= $this->post->getRow($postsId);$post 			= $posts->getRow;/* Organisation */$organisation 	= $this->db->getRow("select organisation_name,logo from ".$this->table_prefix."account_organisation where organisation_id='".$post->organisation."'");$orgName		= @$organisation['organisation_name'];$orgLogo		= uploadURL.'modules/accountorg/thumbs/'.@$organisation['logo'];$organisationName = $post->isHeadline || $post->type=='agency'?$orgName:$this->org_name(@$post->postedBy);$organisationLogo = $post->isHeadline || $post->type=='agency'?$orgLogo:$this->org_logo(@$post->postedBy);/* Random Image */$rsPhoto  	= "select post_id from ".$this->table_prefix."posts where post_type in('photo') and post_id<>'".$postsId."' and publish='1' order by rand() limit 0,8";$arrPost 	= $this->db->getAll($rsPhoto);$arrPhoto[] = $postsId;foreach($arrPost as $p){		$arrPhoto[] = $p['post_id'];}foreach($arrPhoto as $dataPhotoID){		$photo			= $this->post->getRow($dataPhotoID);	$orgLogo		= $this->org_logo(@$photo->postedBy);	$photographer	= !empty($photo->photographer)?$photo->photographer:'-';		@$photoList .= '				<li>			<a href="#detail" class="photo-thumb" 			data-image="'.uploadURL.'modules/posts/'.$photo->image.'" 			data-country="'.$photo->country.'" 			data-logo="'.$orgLogo.'" 			data-photographer="'.$photographer.'" 			data-title="'.$photo->title.'" 			data-content="'.$photo->photolegend.'">				'.$photo->getThumbImage.'							</a>		</li>			';}/* Listing */if($post){	?>	<div class="row-fluid">		<div class="page-container">				<h4 class="heading-title text-white">Photos</h4>			<div class="span2">&nbsp;</div>			<div class="span8">				<div class="container-dark">					<div id="detail" class="photo-items">													<div class="media">							<div class="thumb mini pull-left">								<div id="org-logo">									<img src="<?=$organisationLogo?>">								</div>							</div>							<div class="media-body">							<div class="post-meta">								<p id="photo-country"><?=$post->country?></p>								<p id="photo-title"><?=$post->title?></p>							</div>								<div class="social-share no-margin pull-right"><?=simpleShare()?></div>							</div>						</div>												<p id="photo-content"><?=$post->photolegend?></p>						<div class="post-items">														<div class="image-holder photo-big">								<div id="view-photo" class="view">									<?=$post->getImage?>								</div>							</div>												<div id="photo-author" class="photo-author">Photo: <?=$post->photographer?></div>							<div class="other-photo">								<div class="flexslider-carousel">									<ul class="slides">										<?=$photoList?>									</ul>								</div>							</div>						</div>					</div>				</div>			</div>			<div class="span2">&nbsp;</div>		</div>	</div>		<script>				jQuery.noConflict();		jQuery(document).ready(function($){						$(".photo-thumb").click(function(){								var logo 			= $(this).data("logo");				var country			= $(this).data("country");				var title 			= $(this).data("title");				var content 		= $(this).data("content");				var image 			= $(this).data("image");				var photographer	= $(this).data("photographer");								$("#org-logo").html('<img src="'+logo+'">');				$("#photo-country").html(country);				$("#photo-title").html(title);				$("#photo-content").html(content);				$("#photo-author").html('Photo: '+photographer);				$("#view-photo").html('<img src="'+image+'">');			});		});	</script>	<?}else{	echo 'Data not available.';}?>